import {Component} from "react"
import { Dropdown } from 'primereact/dropdown';
import { InputText } from 'primereact/inputtext';
import { Button } from 'primereact/button';
class Search extends Component{

    state = {
         categories :[
            { name: "Electronics", code: 'ELE000' },
            { name: 'Home Appliances', code: 'HAP000' },
            { name: 'Mobiles', code: 'MOB000' }
        ],
        selectedCategory : "",
        searchValue: ""
    }

    onCityChange = (e) => {
        this.setState({selectedCategory : e.value});
    }

    render(){
        return(
            <div>
                <h1>Welcome To Affiliated Market</h1>
                <div>
                    <span>
                        <Dropdown value={this.state.selectedCategory} 
                            options={this.state.categories} 
                            onChange={this.onCityChange} 
                            optionLabel="name" 
                            placeholder="Select a Category" />
                    </span>
                    <span>
                        <InputText value={this.state.searchValue} onChange={(e) => this.setState({searchValue : e.target.value}) }/>
                    </span>
                    <span>
                        <Button label="Primary" className="p-button-outlined" />
                    </span>
                </div>
            </div>
        )
    }
}

export default Search;
