import {Component} from "react"
import { Dropdown } from 'primereact/dropdown';

class Search extends Component{

    state = {
         categories :[
            { name: "Electronics", code: 'ELE000' },
            { name: 'Home Appliances', code: 'HAP000' },
            { name: 'Mobiles', code: 'MOB000' }
        ],
        selectedCategory : ""
    }

    onCityChange = (e) => {
        this.setState({selectedCategory : e.value});
    }

    render(){
        return(
            <div>
                <h1>Welcome To Affiliated Market</h1>
                <div>
                    <span><Dropdown value={this.state.selectedCategory} options={this.state.categories} onChange={this.onCityChange} optionLabel="name" placeholder="Select a Category" /></span>
                </div>
            </div>
        )
    }
}

export default Search;
